$(function () {

	$(function(){
		/* Удаляет плейсхолдер при фокусе поля формы*/  
		$('input, textarea').each(function(){
			var placeholder = $(this).attr('placeholder');
			$(this).focus(function(){ $(this).attr('placeholder', '');});
			$(this).focusout(function(){			 
				$(this).attr('placeholder', placeholder);  			
			});
		});
	});

	$(function(){
		/*Слайдер*/

		var slider = $('.b-slider');

		slider.owlCarousel({

			paginationSpeed: 800,
			slideSpeed: 800,
			navigation : true,
			singleItem:true,
			navigationText: [
			'<i class="b-slider__navigation b-slider__navigation--prev fa fa-angle-left" aria-hidden="true"></i>',
			'<i class="b-slider__navigation b-slider__navigation--next fa fa-angle-right" aria-hidden="true"></i>'
			],
		});
	});

	$(function() {

		/*Показать текст о сайте*/
		$('.b-about__showmore').click(function(){
			
			$('.b-about__textwrapp').addClass('b-about__textwrapp--open');
			$('.b-about__fill').addClass('hidden');
			$(this).addClass('hidden');
		});
	});
	
});